(function() {
  var Hygiene;

  Hygiene = require("../hygiene");

  describe("cleaning", function() {
    it("should remove trailing whitespace", function() {
      return assert.equal(Hygiene.clean("heyy   \n"), "heyy\n");
    });
    it("should ensure trailing newline", function() {
      return assert.equal(Hygiene.clean("a"), "a\n");
    });
    return it("should keep empties empty", function() {
      return assert.equal(Hygiene.clean(""), "");
    });
  });

}).call(this);

